import React, {Component} from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import styled, {css} from 'styled-components';
import { TransitionGroup, Transition } from 'react-transition-group';
import {facebookIcon, googleIcon, userIcon, passwordIcon} from '../utils/Icon';
import Input from "./Input";
import {ProjectHeaderButton} from "./Button";
import {AnimFade} from "../utils/StyleUtils";


const prerender = window.location.port === '45678';
const initDelay = 300;

class AuthForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: '',
            isLogin: true
        };
        this.toggleAuth = this.toggleAuth.bind(this);
    }

    toggleAuth () {
        const {isLogin} = this.state;
        this.setState({isLogin: !isLogin});
    }

    render() {
        const { formOpen, complete, loading, error } = this.props;
        const { email, isLogin, name, password } = this.state;
        console.log(isLogin)
        return (
            <TransitionGroup component={React.Fragment}>
                {isLogin && formOpen&&
                <Transition appear timeout={0} mountOnEnter unmountOnExit>
                    {status => (
                        <Login status={status}
                               delay={0}>
                            <LoginTitle>Форма входа</LoginTitle>
                            <InputGroup>
                                {userIcon}
                                <UserInput
                                    status={status}
                                    delay={200}
                                    onChange={this.handleInputChange}
                                    autoComplete="name"
                                    id="name"
                                    type="text"
                                    hasValue={!!name}
                                    value={name}
                                    maxLength={320}
                                    required
                                />
                            </InputGroup>
                            <InputGroup>
                                {passwordIcon}
                                <UserInput
                                    status={status}
                                    delay={200}
                                    onChange={this.handleInputChange}
                                    id="password"
                                    type="password"
                                    required
                                />
                            </InputGroup>
                            <ButtonGroup>
                                <AuthDescription onClick={this.toggleAuth}>Зарегистрироваться</AuthDescription>
                                <AuthButton
                                    sending={loading}
                                    loading={loading}
                                    status={status}
                                    delay={400}
                                    icon="send"
                                    type="submit">
                                    Войти
                                </AuthButton>
                            </ButtonGroup>
                            <AuthSocial>
                                <AuthDescription>Войти с помощью соцсетей</AuthDescription>
                                <FacebookIcon>{facebookIcon}</FacebookIcon>
                                <GoogleIcon>{googleIcon}</GoogleIcon>
                            </AuthSocial>
                        </Login>
                    )}
                </Transition>
                }
                {!isLogin && formOpen &&
                <Transition appear timeout={0} mountOnEnter unmountOnExit>
                    {status => (
                        <Login>
                            <LoginTitle>Регистрация</LoginTitle>
                            <InputGroup>
                                {userIcon}
                                <UserInput
                                    status={status}
                                    delay={200}
                                    onChange={this.handleInputChange}
                                    autoComplete="name"
                                    id="name"
                                    type="text"
                                    hasValue={!!name}
                                    value={name}
                                    maxLength={320}
                                    required
                                />
                            </InputGroup>
                            <InputGroup>
                                {passwordIcon}
                                <UserInput
                                    status={status}
                                    delay={200}
                                    onChange={this.handleInputChange}
                                    id="password"
                                    type="password"
                                    required
                                />
                            </InputGroup>
                            <ButtonGroup>
                                <AuthDescription onClick={this.toggleAuth}>Вxoд</AuthDescription>
                                <AuthButton
                                    sending={loading}
                                    loading={loading}
                                    status={'entering'}
                                    delay={400}
                                    icon="send"
                                    type="submit">
                                    Регистрация
                                </AuthButton>
                            </ButtonGroup>
                            <AuthSocial>
                                <AuthDescription>Зарегистрироваться с помощью соцсетей</AuthDescription>
                                <Link to={'/facebook'} className="socialLink">
                                    <FacebookIcon>
                                        {facebookIcon}
                                    </FacebookIcon>
                                </Link>
                                <Link to={'/google'} className="socialLink">
                                    <GoogleIcon>
                                        {googleIcon}
                                    </GoogleIcon>
                                </Link>
                            </AuthSocial>
                        </Login>
                    )}
                </Transition>
                }
            </TransitionGroup>
        )
    }
};

const Login = styled.form`
  top: 120px;
  right: 5px;
  width: 360px;
  z-index: 1025;
  color: rgba(#fff, .85);
  display: inline-block;
  font-size: 16px;
  position: fixed;
  padding: 0 10px;
  text-decoration: none;
  border-radius: 10px;
  @include background-clip(padding-box);
  @include box-shadow(inset -1px 1px 0px rgba(255, 255, 255, 0.3),
  inset 1px 1px 0px rgba(255, 255, 255, 0.3)
  );
  border: 1px solid #000;
  background-color: rgba(#000, .1);
  background: #111111;
  opacity: 0.99;
  @include background-image(linear-gradient(top, rgba(#000, .1), rgba(#000, .3)));
  .extra {

  }

  &:after{
    content: '';
    position: absolute;
    top: -13px;
    right: 22px;
    width: 25px;
    height: 25px;
    //opacity: 0.9;
    background: #111;
    border-left: 1px solid #000;
    border-bottom: 1px solid #000;
    transform: rotate(135deg);
  }
}
`;


const LoginTitle = styled.h2 `
  font-size: 26px;
  width: 100%;
  text-align: center;
  margin: 10px 0;
  text-transform: uppercase;
`

const InputGroup = styled.div`
  position: relative;
  display: flex;
  flex-direction: row;
  width: 100%;

  svg {
    width: 16px;
    height: 16px;
    fill: #ffffff;
    position: absolute;
    top: 25px;
    left: 0;
    z-index: 1025;
  }
`;

const UserInput = styled(Input)`
  position: unset;
  top: 0;
  width: 100%;
  padding-left: 25px;

  input {
    background-color: transparent !important;
    min-height: 35px;
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  position: relative;
  margin-bottom: 10px;
  margin-top: 10px;
`;

const AuthDescription = styled.span `
    font-size: 12px;
    text-decoration: underline;
    cursor: pointer;
`

const AuthButton = styled(ProjectHeaderButton)`
  height: 40px;
  transition-property: transform, opacity;
  transition-timing-function: ${props => props.theme.curveFastoutSlowin};
  transition-delay: ${props => props.status === 'entered' ? '0ms' : `${props.delay + initDelay}ms`};
  transition-duration: ${props => props.status === 'entered' ? '0.4s' : '0.8s'};
  transform: translate3d(0, 80px, 0);
  opacity: 1;
  padding-right: 15px;
  padding-left: 15px;
  span {
    font-size: 12px;
  }

  svg {
    width: 16px;
    height: 16px;
  }


  ${props => props.sending && `
    svg {
      transition: transform ${props.curveFastoutSlowin}, opacity 0.3s ease 0.8s;
      transition-duration: 0.8s;
      transform: translate3d(150px, 0, 0);
      opacity: 0;
    }

    div {
      opacity: 0;
    }
  `}

  ${props => props.sending && css`
   div {
      animation: ${AnimFade} 0.5s ease 0.6s forwards;
    }
  `}

  ${props => (props.status === 'entering' ||
    props.status === 'entered') && !prerender && `
    transform: translate3d(0, 0, 0);
    opacity: 1;
  `}

  ${props => props.status === 'exiting' && `
    transition-duration: 0.4s;
    transition-delay: 0s;
    transform: translate3d(0, -40px, 0);
    opacity: 0;
  `}
`;


const AuthSocial = styled.div`
  outline: none;
  margin: 0;
  padding: 0;
  list-style: none;
  bottom: 0;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  width: 100%;
  height: 50px;
  background: rgba(#000,.3);
  left: 0;
  border-radius: 0 0 10px 10px;
  @include box-shadow(inset -1px 1px 0px rgba(255, 255, 255, 0.3),
  inset 1px 0 0px rgba(255, 255, 255, 0.3)
  );
  border-top: 1px solid #000;
  word-spacing: -1;

  .socialLink {
    height: 100%;
  }
`;

const FacebookIcon = styled.a`
      top: 0;
      cursor: pointer;
      background-position: left top;
      background-repeat: no-repeat;
      width: 49px;
      display: flex;
      align-items: center;
      justify-content: center;
      font-size: 0;
      height: 100%;
      background-image: url(data:image/png;base64,);
      &:before {
        position: absolute;
        top: 7px;
        left: 12px;
        text-indent: 0;
        color: rgba(#fff, .8);
        text-shadow: 0 -1px 0 rgba(0,0,0,0.3);
        font-size: 30px;
        left: 19px;
      }
      right: 50px;
      svg {
        width: 30px;
        height: 30px;
        fill: #fff;
      }
`;

const GoogleIcon = styled(FacebookIcon)`
  right: 0;
  background-image: url(data:image/png;base64,);
`
export default AuthForm;


